import React from 'react';
import ExpenseItem from  './ExpenseItem';
import ExpenseCounter from './ExpenseCounter';

class DisplayContainer extends React.Component {
	constructor(props) {
    super(props);
     this.state = {
      expenses: [],
	 };
	 this.updateExpenseFromLocalStorage = this.updateExpenseFromLocalStorage.bind(this);
	 this.handleStorageChange = this.handleStorageChange.bind(this);
	}

	componentDidMount() {
		this.updateExpenseFromLocalStorage();
		window.addEventListener("storage", (event) => this.handleStorageChange(event)); // Arrow function to preserve 'this' context
		console.log("Component mounted");
	  }

	  componentWillUnmount() {
		window.removeEventListener("storage", this.handleStorageChange); // Clean up event listener
		console.log("Component unmounted");
	  }

	  handleStorageChange() {
		console.log("Storage changed");
		this.updateExpenseFromLocalStorage();
	  }

	updateExpenseFromLocalStorage() {
		console.log("Updating expenses from localStorage");
		const keys = Object.keys(localStorage).filter(key =>
		  key.startsWith("expense_")
		);
		const expenseData = keys.map(key => JSON.parse(localStorage.getItem(key)));
		console.log("Expense data from localStorage:", expenseData);
		this.setState({ expenses: expenseData }, () => {
		  console.log("Updated state:", this.state.expenses);
		});
	}



    render() {
		console.log("render")
	  const {expenses} = this.state;

      return (
	  		<div className="display-container">
                <h1>Expenses List</h1>
				<ExpenseCounter
					count= {expenses.length}
				/>
				<div className="display-container-items">
					{expenses.map((expence) => (
						<ExpenseItem
							title = {expence.title}
							category = {expence.category}
							amount = {expence.amount}
							date = {expence.date}
						/>

					))}
				</div>
            </div>
			);
    }
  }



  export default DisplayContainer;
