import React from 'react';
import ExpenseItem from  './ExpenseItem';
import ExpenseCounter from './ExpenseCounter';




class DisplayContainer extends React.Component {
	constructor(props) {
    super(props);
     this.state = {
      expenses: [],
	 };
	 this.updateExpenseFromLocalStorage = this.updateExpenseFromLocalStorage.bind(this);
	}

	componentDidMount(){
		this.updateExpenseFromLocalStorage();
		window.addEventListener("storage", this.updateExpenseFromLocalStorage);
	}
	componentWillUnmount(){
		window.removeEventListener("storage", this.updateExpenseFromLocalStorage);
	}

	updateExpenseFromLocalStorage(){
		const keys = Object.keys(localStorage).filter(key=>key.startsWith("expense_"));
		const expenseData = keys.map(key => JSON.parse(localStorage.getItem(key)));
		this.setState({expenses:expenseData});
	}

    render() {

	  const {expenses} = this.state;

      return (
	  		<div className="display-container">
                <h1 className="display-container-header">Expenses List</h1>
				<ExpenseCounter
					count= {expenses.length}
				/>
				<div></div>
				{expenses.map((expence) => (
					<ExpenseItem
						title = {expence.title}
						category = {expence.category}
						amount = {expence.amount}
						date = {expence.date}
					/>

				))}
            </div>
			);
    }
  }

  export default DisplayContainer;
